    private void botonGuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonGuardarActionPerformed

        String indice = txtMarcadorIndice.getText();
        // estamos trasformando el String en un entero
        int indi = Integer.parseInt(indice);

        rut[indi] = txtRut.getText();
        nombre[indi] = txtNombre.getText();
        apellido[indi] = txtApellido.getText();
        direccion[indi] = txtDireccion.getText();
        telefono[indi] = txtTelefono.getText();
        fechaNac[indi] = txtFechaNacimiento.getText();


    }//GEN-LAST:event_botonGuardarActionPerformed

