    private void botonAdelanteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonAdelanteActionPerformed

        String indice = txtMarcadorIndice.getText();
        // estamos trasformando el String en un entero
        int indi = Integer.parseInt(indice);
        //Agregamos una condición
        if (indi < 9) {
            indi = indi + 1;
            //volvemos a convertirlo en String
            indice = String.valueOf(indi);
            // seteamos el valor y actualizamos
            txtMarcadorIndice.setText(indice);
            
            txtRut.setText(rut[indi]);
            txtNombre.setText(nombre[indi]);
            txtApellido.setText(apellido[indi]);
            txtDireccion.setText(direccion[indi]);
            txtTelefono.setText(telefono[indi]);
            txtFechaNacimiento.setText(fechaNac[indi]);
        }
    }//GEN-LAST:event_botonAdelanteActionPerformed

    private void botonAtrasActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonAtrasActionPerformed

        String indice = txtMarcadorIndice.getText();
        // estamos trasformando el String en un entero
        int indi = Integer.parseInt(indice);
        //Agregamos una condición
        if (indi > 0) {
            indi = indi - 1;
            //volvemos a convertirlo en String
            indice = String.valueOf(indi);
            // seteamos el valor y actualizamos
            txtMarcadorIndice.setText(indice);
            
            txtRut.setText(rut[indi]);
            txtNombre.setText(nombre[indi]);
            txtApellido.setText(apellido[indi]);
            txtDireccion.setText(direccion[indi]);
            txtTelefono.setText(telefono[indi]);
            txtFechaNacimiento.setText(fechaNac[indi]);
        }
    }//GEN-LAST:event_botonAtrasActionPerformed


